WHERE a.cid=#{cid,jdbcType=INTEGER}  
AND DATE_FORMAT(a.`createtime`,'%Y-%m')=#{date,jdbcType=VARCHAR}
ORDER BY a.`createtime` DESC

SELECT * FROM tb_data a 
WHERE a.cid=#{cid,jdbcType=INTEGER}  
